const { exec, spawn } = require("child_process");

const os = {};

os.openBrowser = (url) => {
  const start =
    process.platform === "darwin"
      ? "open"
      : process.platform === "win32"
      ? "start"
      : "xdg-open";

  exec(start + " " + url);
};

os.openWindow = (directory) => {
  spawn("open", [directory]);
};

module.exports = os;
