const prompts = require("prompts");
const prompt = {};

prompt.boolean = async (message) => {
  const { val } = await prompts({
    type: "confirm",
    name: "val",
    message,
    initial: false,
  });
  return val;
};

prompt.string = async (message) => {
  const { val } = await prompts({
    type: "text",
    name: "val",
    message,
  });
  return val;
};

prompt.select = async (message, array) => {
  const choices = array.map((choice) => ({ title: choice, value: choice }));
  const { val } = await prompts({
    type: "select",
    name: "val",
    message,
    choices,
  });
  return val;
};

prompt.multiselect = async (message, array) => {
  const choices = array.map((choice) => ({ title: choice, value: choice }));
  const { val } = await prompts({
    type: "multiselect",
    name: "val",
    message,
    choices,
  });
  return val;
};

module.exports = prompt;
